    private void jbtnSolve1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnSolve1ActionPerformed
        int a1 = Integer.parseInt(jtxtCoefficient1.getText());
        int n1 = Integer.parseInt(jtxtExponent1.getText());
        int a2 = Integer.parseInt(jtxtCoefficient2.getText());
        int n2 = Integer.parseInt(jtxtExponent2.getText());

        String x1 = jtxtVariable1.getText();
        String x2 = jtxtVariable2.getText(); 

        String v2 = (a2 * a2) + x2 + "^" + (n2 * 2);

        String result;
        if (x1.equals(x2)) {
            int coefficient1 = a1 * a2 * n2;
            int exponent1 = n1 + n2 - 1;
            int coefficient2 = a2 * a1 * n1;
            int exponent2 = exponent1;


            String udv = coefficient1 + x1 + "^" + exponent1;
            String vdu = coefficient2 + x2 + "^" + exponent2;

            
            result = "(" + vdu + " - " + udv + ") / " + v2;
        } else {
            
            int coefficient1 = a1 * a2 * n2;
            int exponent1 = n2 - 1;
            int coefficient2 = a2 * a1 * n1;
            int exponent2 = n1 - 1;


            String udv = coefficient1 + "(" + x1 + "^" + n1 + ")" + "(" + x2 + "^" + exponent1 + ")";
            String vdu = coefficient2 + "(" + x1 + "^" + exponent2 + ")" + "(" + x2 + "^" + n2 + ")";


            result = "(" + vdu + " - " + udv + ") / " + v2;
        }


        jlblResult.setText(result);

    }//GEN-LAST:event_jbtnSolve1ActionPerformed

